package org.pack.java.annotationProcessing;import java.lang.reflect.Method;public class AnnotationProcessor {    public static void main(String[] args) {        try {            Class<DataClass> obj = DataClass.class;            for (Method method : obj.getDeclaredMethods()) {                if (method.isAnnotationPresent(Data.class)) {                    Data annotation = method.getAnnotation(Data.class);                    System.out.println(method.getName());                    System.out.println(annotation.value());                }            }        }catch (Exception e){            e.printStackTrace();        }    }}