package org.pack.java.schemaGenerator;import java.util.Random;public class Content {	private long id= new Random().nextLong();	private String content;	public Content(long id, String content) {		this.id = id;		this.content = content;	}	public long getId() {		return id;	}	public String getContent() {		return content;	}	public void setId(long id) {		this.id = id;	}	public void setContent(String content) {		this.content = content;	}	@Override	public String toString() {		return "Content{" +			   "id=" + id +			   ", content='" + content + '\'' +			   '}';	}}